const form=document.getElementById("enquete"),radios=document.querySelectorAll('input[name="sexo"]');let sexo="Masculino";form.addEventListener("submit",(async e=>{e.preventDefault();const o=form.nome.value.trim(),a=form.idade.value.trim(),r=form.profissao.value.trim(),t=form.comentario.value.trim();radios.forEach((e=>{e.checked&&(sexo=e.id)}));if([o,a,r,t].some((e=>""===e)))alert("Preencha todos os dados!!!");else if(a<=0||a>=100)alert("Digite uma idade v√°lida");else try{(await fetch("/cadastrar",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({nome:o,sexo:sexo,idade:a,profissao:r,comentario:t})})).ok?(alert("Enquete inserida com sucesso!"),location.reload()):alert("Erro ao comentar!")}catch(e){alert("Erro interno no servidor.")}}));